def atualiza_valor(valor, mes_ano_inicial, mes_ano_final):
    coef_inicial = coeficientes.get(mes_ano_inicial)
    coef_final = coeficientes.get(mes_ano_final)
    if coef_inicial is None or coef_final is None:
        raise ValueError("Data inicial ou final n√£o encontrada nos coeficientes.")
    return round(valor * (coef_final / coef_inicial), 2)

